import { useState } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
`
const Form = styled.form`
    width: 500px;
    display: grid;
    flex-direction: column;
    gap: 10px;
    text-align: center;
`
const Title = styled.h1`
    font-size: 40px;
    margin-bottom: 20px;
`
const Input = styled.input`
    font-size: 16px;
    padding: 10px;
    border: none;
    border-radius: 5px;
    &[type="file"]{
        display: none;
    }
    &[type="submit"]{
        background-color: #e37676;
    }
`
const ProfilePhoto = styled.div`
    width: 150px;
    height: 150px;
    border: 1px solid gray;
    border-radius: 50%;
    background-image: {};
`
const Label = styled.label`
    display: flex;
    justify-content: center;
`
export default function CreateAccount(){
    const [uploading, setUploading] = useState("계정 만들기");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [nickname, setNickname] = useState("");
    const [profilePhoto, setprofilePhoto]=useState([]);
    return(
        <Wrapper>
            <Form>
                <Title>createAccount</Title>
                <Input type="text" name="username" placeholder="이름을 입력해주세요."/>
                <Input type="email" name="email" placeholder="E-mail을 입력해주세요."/>
                <Input type="password" name="password" placeholder="사용할 비밀번호를 입력해주세요."/>
                <Input type="text" name="nickname" placeholder="사용하실 닉네임을 입력해주세요."/>
                {/* 프로필사진 미리보기 만들기 */}
                <Label htmlFor="profileImage">
                    <ProfilePhoto/>
                </Label>
                <p>프로필 사진 설정하기</p>
                <Input type="file" name="profilePhoto" accept="image/*" id="profileImage"/>
                <Input type="submit" value={uploading}/>
            </Form>
        </Wrapper>
    );
}
